SELECT throw.id, throw.success, throw.testType, throw.failedAt, throw.deviation, objPos.x as objPosX, objPos.y as objPosY, goalPos.x as goalPosX, goalPos.y as goalPosY, robotConfig.j1, robotConfig.j2, robotConfig.j3, robotConfig.j4, robotConfig.j5, robotConfig.j6, robotConfig.x, robotConfig.y, robotConfig.z, robotConfig.q1, robotConfig.q2, robotConfig.q3, throw.totalThrowTime, throw.calibImgTime, throw.findObjTime, throw.pathCalcTime, throw.grabTime, throw.throwTime, throw.apiLogTime FROM throw INNER JOIN point objPos ON throw.objPos = objPos.id INNER JOIN point goalPos ON throw.goalPos = goalPos.id INNER JOIN robotConfig ON throw.robotStartConfig = robotConfig.id